import {
  GET_PROFILE,
  PROFILE_ERROR,
  CLEAR_PROFILE,
  GET_CHAT,
  GET_FAIL
} from '../actions/types';

const initialState = {
  profile: null,
  loading: true,
  error: {}
}

export default function (state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
    case GET_PROFILE:
    case GET_CHAT:
      return {
        ...state,
        profile: payload,
        loading: false
      };
    case PROFILE_ERROR:
    case GET_FAIL:
      return {
        ...state,
        error: payload,
        loading: false
      };
    case CLEAR_PROFILE:
      return {
        ...state,
        profile: null,
        loading: false
      }
    default:
      return state;
  }
}
