import React, { Fragment, useState } from 'react';
import { Link, withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createChat } from '../../actions/profile';

const CreateProfile = ({ createChat, history }) => {
  const [formData, setFormData] = useState({
    title: '',
    password: ''
  });

  const {
    title,
    password
  } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    createChat(form, history);
  }

  return (
    <Fragment>
      <h1 className="large text-primary">
        Create New Chat
    </h1>
      <p className="lead">
        Fill Out Form Below
    </p>
      <div className="form">
        <form action="dashboard.html" className="form">
          <div className="form-group">
            <input type="text" placeholder="Chat Name" name='title' value={title} onChange={e => onChange(e)} required />
          </div>
          <div className="form-group">
            <input type="password" placeholder="Chat Password" name='password' value={password} onChange={e => onChange(e)} required minLength="6" />
          </div>
          <div className="form-group">
            <input type="password" placeholder="Confirm Password" required minLength="6" />
          </div>
          <input type="submit" value="Create" className="btn btn-primary" />
        </form>
      </div>
    </Fragment>
  )
}

CreateChat.propTypes = {
  createPChat: PropTypes.func.isRequired
}



export default connect(null, { createChat })(withRouter(CreateChat));
