Chat.propTypes = {
  getChatById: PropTypes.func.isRequired,
  chat: PropTypes.object.isRequired,

}

const mapStateToProps = state => ({
  profile: state.profile
})

export default connect(mapStateToProps, { getChatById })(Chat);


