  })
  */
  console.log(chat);
  return <h1>Test</h1>
}

Post.propTypes = {
  getChatById: PropTypes.func.isRequired,
  chat: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
  profile: state.profile
})

export default connect(mapStateToProps, { getChatById })(Post);
