import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { getCurrentChat } from '../../actions/profile';


const Chats = ({
  getCurrentChat,
  auth: { user },
  chat: { chat, loading } }) => {
  const chats = chat.map(ch => (
    <li className="text-primary" key={ch._id}>
      <Link to="/chat">{ch}</Link>
    </li>
  ));
  return (
    <Fragment>
      <Link to="/create-chat">Create New Chat</Link>
      <br />
      <Link to="/join-chat">Join a Chat</Link>
      <div className="profiles">
        <div className="profile bg-light">
          <div>
            <h2>Your chats:</h2>
          </div>
          <ul>
            {chats}
          </ul>
        </div>
      </div>
    </Fragment>
  )
}

Chats.propTypes = {
  getCurrentChat: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  chat: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
  auth: state.auth,
  profile: state.profile
});

export default connect(mapStateToProps, { getCurrentChat })(Chats);
