import React, { Fragment, useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';



const Chats = ({ chat }) => {
  const [clickData, setClick] = useState({
    clicked: false,
    clickId
  });

  const { clicked } = clickData;

  const handleClick = e => {
    setClick({
      ...clickData,
      clicked: true,
      clickId: e.target.id
    })
  }
  const chats = chat.map(ch => (
    <li className="text-primary" key={ch._id}>
      <p onClick={handleClick}>{ch}</p>
    </li>
  ));
  return clicked ? <div><h1><Chat id={clickId} /></h1></div> : <Fragment>
    <Link to="/create-chat">Create New Chat</Link>
    <br />
    <Link to="/join-chat">Join a Chat</Link>
    <div className="profiles">
      <div className="profile bg-light">
        <div>
          <h2>Your chats:</h2>
        </div>
        <ul>
          {chats}
        </ul>
      </div>
    </div>
  </Fragment>
};

Chats.propTypes = {
  chat: PropTypes.array.isRequired
}



export default Chats;
