import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { addPost } from '../../actions/chat';

const PostForm = ({ chatId, addPost }) => {
  const [formData, setText] = useState({
    title: '',
    text: ''
  });

  const onChange = e => setText({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    addPost(chatId, { title, text });
    setText({
      [title]: '',
      [text]: ''
    })

  }
  return (
    <div>
      <div className="post-form">
        <div className="post-form-header bg-primary">
          <h3>Create a Post</h3>
        </div>
        <form className="form my-1" onSubmit={e => {
          e.preventDefault();
          addPost({ title, text }, chatId);
          setText('');
        }}
        >
          <textarea
            name='title'
            cols="30"
            rows="2"
            placeholder="Create post title"
            value={title}
            onChange={e => onChange(e)}
          />
          <textarea
            cols="30"
            rows="5" placeholder="Create a post"
            name='text'
            value={text}
            onChange={e => onChange(e)}
          />
          <input type="submit" value="Submit" class="btn btn-dark my-1" />
        </form>
      </div>
    </div>
  )
}

PostForm.propTypes = {
  addPost: PropTypes.func.isRequired
}

export default connect(null, { addPost })(PostForm);
